function appReg()
{
  var rID = $("#registrationid").val();
  var rAadhar= $("#aadharnum").val();
  $.ajax({
    type: "POST",
    data: {appid:rID,appAadhar:rAadhar,action:'checkregID'},
    url: "ajax.php",
    dataType:"json",
    success: function(data){
    //alert(data);
      if(data==null) 
      {
        alert("Registration ID Not Found");
      }
      else
      {
        $("#registrationid").val(data['registrationId']);
        $("#aadharnum").val(data['aadhar']);
        $("#cname").val(data['cname']);
        $("#email").val(data['email']);
        $("#age").val(data['age']);
        $("#qualification").val(data['qualification']);
        $("#phone").val(data['phone']);
      }
     }
    });
}
function newRegistration()
{
  if ($('#registrationid').val() == ""){
    $("#registrationid").css("border","1px solid #FF0000");
    return false;
  }
  else
  {
    $("#registrationid").css("border","1px solid #CCCCCC");
  }
  var form = $('#regform');
  $.ajax({
    type: "POST",
    data: form.serialize(),
    url: "ajax.php",
    success: function(data){
      if(data=='Suc') {
        alert("Thank you for registration");
        location.replace("http://vikasajobs.com/index.php");
      }
      else
      {
        alert("Already existing Registraiton ID");  
        $("#registrationid").val("");
        $("#aadharnum").val("");      
        $("#email").val("");
        $("#candidateName").val("");
        $("#age").val("");
        $("#qualification").val("");
        $("#phone").val("");
      }
     }
    });
}
